import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { LoginInfo } from 'src/app/Models/login.model';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  @Input() loginInfoForHome: LoginInfo | undefined;
  @Output() logoutStatusEmitter : EventEmitter<boolean> = new EventEmitter();

  constructor() { }

  currentPage: number = 1;
  requestCount : number = 0;
  search : string ="";

  ngOnInit(): void {
    // this is initilization function 
  }
  currentProfilePerson(demo: LoginInfo){
    this.currentPage = 4;
  }

  currentRequestCount(demo:number){
    this.requestCount = demo;
  }

  currentPageChange(demo : number){
    this.currentPage = demo;
  }

  logoutStatusHomePage(demo : boolean){
    this.logoutStatusEmitter.emit(demo);
  }

  searchResultInfo : LoginInfo[] = [];
  changeSearchContentHome(demo : LoginInfo[]){
    this.searchResultInfo= demo;
  }

}

<body>
    <app-header [loginInfoForHeader]="loginInfoForHome" [requestCount]="requestCount" (currentPageEmitter)="currentPageChange($event)" (logoutStatusEmitter)="logoutStatusHomePage($event)" (searchContentEmitter)="changeSearchContentHome($event)"></app-header>

    <div *ngIf="currentPage==1">
        <app-main-body-part [loginInfoForMainBody]="loginInfoForHome"></app-main-body-part>
    </div>

    <div *ngIf="currentPage==3">
        <app-message [loginInfoForMessage]="loginInfoForHome"></app-message>
    </div>

    <div *ngIf="currentPage==2">
        <app-friend-list [loginInfoForFriendList]="loginInfoForHome" (requestCountNumber)="currentRequestCount($event)" (thisPersonProfileInfo)="currentProfilePerson($event)"></app-friend-list>
    </div>

    <div *ngIf="currentPage==4">
        <app-profile></app-profile>
    </div>

    <div *ngIf="currentPage==5">
        <app-search-result [searchResultInfoForSearch]="this.searchResultInfo" (thisPersonProfileInfoSearch)="currentProfilePerson($event)"></app-search-result>
    </div>


</body>
