import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { AdminInfo } from 'src/app/Models/admin.model';
import { LoginInfo } from 'src/app/Models/login.model';
import { AdminService } from 'src/app/Service/admin.service';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent implements OnInit {

  @Input() loginInfoForSidebar : LoginInfo | undefined;
  @Output() mainBodyOpacity : EventEmitter<boolean> = new EventEmitter();
  AdminCode : number = 0;
  adminPassword : string = "";

  constructor(private adminService: AdminService) { }

  ngOnInit(): void {
  }

  addAdmin(){
    this.AdminCode = 1;
    this.mainBodyOpacity.emit(true);
  }

  make_cancel(){
    this.AdminCode = 0;
    this.mainBodyOpacity.emit(false);
  }

  demoAdmin : AdminInfo = {
    adminId: ''
  }

  addAdminCheck(){
    if (this.adminPassword=="admin"){
      this.make_cancel();
      this.demoAdmin.adminId = this.loginInfoForSidebar?.id as string;
      this.adminService.addAdmin(this.demoAdmin).subscribe(result =>{
        console.log(result);
      });
    }
    else {
      alert("Please Enter The Right Admin Password")
      this.adminPassword="";
    }
  }
}
<div class="sidebar">
    <div class="sidebarRow">
      <img
        class="user__avatar"
        alt="data:image/png;base64,"
        src="{{loginInfoForSidebar?.profilePicture}}"
      />
      <h4>{{loginInfoForSidebar?.name}}</h4>
    </div>

    <div class="sidebarRow">
      <span class="material-icons"> local_hospital </span>
      <h4>Covid - 19 Information Center</h4>
    </div>

    <div class="sidebarRow">
      <span class="material-icons"> emoji_flags </span>
      <h4>Pages</h4>
    </div>

    <div class="sidebarRow">
      <span class="material-icons"> people </span>
      <h4>People</h4>
    </div>

    <div class="sidebarRow">
      <span class="material-icons"> chat </span>
      <h4>Messenger</h4>
    </div>

    <div class="sidebarRow">
      <span class="material-icons"> storefront </span>
      <h4>Marketplace</h4>
    </div>

    <div class="sidebarRow" (click)="addAdmin()">
      <span class="material-icons"> add </span>
      <h4>Admin</h4>
    </div>
  </div>

  <div *ngIf="AdminCode">
    <div class="color_opacity"></div>
     <div class="maindiv">
      <div class="for_cancel" (click)="make_cancel()">
        <p style="color: red;">X</p>
      </div>
      <div class="forAdmin">
        <p><b>Enter Admin Password</b></p>
      </div>
      <div class="form__group field">
        <input type="password" class="form__field" placeholder="Password" name="name" id='name' required="" autocomplete="off" [(ngModel)]="adminPassword"/>
        <label for="name" class="form__label">Password</label>
      </div>
      <div class="for_button" (click)="addAdminCheck()">
        <button class="btn_submit">Submit</button>
      </div>
    </div>
  </div>
